def longest_word(string):
    max_length = 0
    current_length = 0

    for char in string:
        if char != ' ':
            current_length += 1
        else:
            if current_length > max_length:
                max_length = current_length
            current_length = 0

    if current_length > max_length:
        max_length = current_length

    return max_length

# Test the function
string = "This is a sample string with words of varying lengths"
print(longest_word(string))

