def split_sentences(paragraph):
    sentences = []
    current_sentence = []
    for word in paragraph.split():
        current_sentence.append(word)
        if word.endswith('.') or word.endswith('!') or word.endswith('?'):
            sentences.append(' '.join(current_sentence))
            current_sentence = []
    if current_sentence:
        sentences.append(' '.join(current_sentence))
    return sentences
